tasks.solution:
class User:
    def __init__(self, name):
        self.name = name 
    @classmethod
    def from_email(cls, email):
        name = email.split('@')[0]
        return cls(name) 
u = User.from_email('test@example.com')
print(u.name)

class validator:
    @staticmethod
    def is_valid_email(email):
        return '@' in email

print(validator.is_valid_email('test@example.com'))

class Point:
    def __init__(self, x, y):
        self.x = x
        self.y = y

    @classmethod
    def from_string(cls, data_string):
        x_str, y_str = data_string.split(',')
        return cls(int(x_str), int(y_str))

p = Point.from_string('10,20')
print(p.x)

